"use strict";let body=document.querySelector(".body");class Modal{constructor(e){this.elem=document.getElementById(e),this.loadHideListeners()}show(e){this.elem.classList.add("_visibility"),body.classList.add("_removeScrole"),e&&e()}hide(e){this.elem.classList.remove("_visibility"),body.classList.remove("_removeScrole"),this.createCloseEvent(),e&&e()}createCloseEvent(){let e=new Event("modalClose");document.dispatchEvent(e)}loadHideListeners(){this.elem.addEventListener("click",e=>{e.target.classList.contains("modal__close")&&this.hide()}),this.elem.addEventListener("click",e=>{e.target.classList.contains("modal")&&this.hide()})}}function initialModal(e){const t=new Modal("myModal_"+e);document.getElementById("showModal_"+e).addEventListener("click",()=>{t.show()})}